00138         private static SteamVR CreateInstance()
00139         {
00140             initializedState = InitializedStates.Initializing;
00141 
00142             try
00143             {
00144                 var error = EVRInitError.None;
00145                 if (!SteamVR.usingNativeSupport)
00146                 {
00147                     ReportGeneralErrors();
00148                     initializedState = InitializedStates.InitializeFailure;
00149                     SteamVR_Events.Initialized.Send(false);
00150                     return null;
00151                 }
00152 
00153                 // Verify common interfaces are valid.
00154 
00155                 OpenVR.GetGenericInterface(OpenVR.IVRCompositor_Version, ref error);
00156                 if (error != EVRInitError.None)
00157                 {
00158                     initializedState = InitializedStates.InitializeFailure;
00159                     ReportError(error);
00160                     ReportGeneralErrors();
00161                     SteamVR_Events.Initialized.Send(false);
00162                     return null;
00163                 }
00164 
00165                 OpenVR.GetGenericInterface(OpenVR.IVROverlay_Version, ref error);
00166                 if (error != EVRInitError.None)
00167                 {
00168                     initializedState = InitializedStates.InitializeFailure;
00169                     ReportError(error);
00170                     SteamVR_Events.Initialized.Send(false);
00171                     return null;
00172                 }
00173 
00174                 settings = SteamVR_Settings.instance;
00175 
00176                 if (Application.isEditor)
00177                     IdentifyEditorApplication();
00178 
00179                 SteamVR_Input.IdentifyActionsFile();
00180 
00181                 if (SteamVR_Settings.instance.inputUpdateMode != SteamVR_UpdateModes.Nothing || SteamVR_Settings.instance.poseUpdateMode != SteamVR_UpdateModes.Nothing)
00182                 {
00183                     SteamVR_Input.Initialize();
00184 
00185 #if UNITY_EDITOR
00186                     if (SteamVR_Input.IsOpeningSetup())
00187                         return null;
00188 #endif
00189                 }
00190             }
00191             catch (System.Exception e)
00192             {
00193                 Debug.LogError("<b>[SteamVR]</b> " + e);
00194                 SteamVR_Events.Initialized.Send(false);
00195                 return null;
00196             }
00197 
00198             _enabled = true;
00199             initializedState = InitializedStates.InitializeSuccess;
00200             SteamVR_Events.Initialized.Send(true);
00201             return new SteamVR();
00202         }
00203 
00204         static void ReportError(EVRInitError error)
00205         {
00206             switch (error)
00207             {
00208                 case EVRInitError.None:
00209                     break;
00210                 case EVRInitError.VendorSpecific_UnableToConnectToOculusRuntime:
00211                     Debug.LogWarning("<b>[SteamVR]</b> Initialization Failed!  Make sure device is on, Oculus runtime is installed, and OVRService_*.exe is running.");
00212                     break;
00213                 case EVRInitError.Init_VRClientDLLNotFound:
00214                     Debug.LogWarning("<b>[SteamVR]</b> Drivers not found!  They can be installed via Steam under Library > Tools.  Visit http://steampowered.com to install Steam.");
00215                     break;
00216                 case EVRInitError.Driver_RuntimeOutOfDate:
00217                     Debug.LogWarning("<b>[SteamVR]</b> Initialization Failed!  Make sure device's runtime is up to date.");
00218                     break;
00219                 default:
00220                     Debug.LogWarning("<b>[SteamVR]</b> " + OpenVR.GetStringForHmdError(error));
00221                     break;
00222             }
00223         }
