00255         public string GetTrackedDeviceString(uint deviceId)
00256         {
00257             var error = ETrackedPropertyError.TrackedProp_Success;
00258             var capacity = hmd.GetStringTrackedDeviceProperty(deviceId, ETrackedDeviceProperty.Prop_AttachedDeviceId_String, null, 0, ref error);
00259             if (capacity > 1)
00260             {
00261                 var result = new System.Text.StringBuilder((int)capacity);
00262                 hmd.GetStringTrackedDeviceProperty(deviceId, ETrackedDeviceProperty.Prop_AttachedDeviceId_String, result, capacity, ref error);
00263                 return result.ToString();
00264             }
00265             return null;
00266         }
00267 
00268         public string GetStringProperty(ETrackedDeviceProperty prop, uint deviceId = OpenVR.k_unTrackedDeviceIndex_Hmd)
00269         {
00270             var error = ETrackedPropertyError.TrackedProp_Success;
00271             var capactiy = hmd.GetStringTrackedDeviceProperty(deviceId, prop, null, 0, ref error);
00272             if (capactiy > 1)
00273             {
00274                 var result = new System.Text.StringBuilder((int)capactiy);
00275                 hmd.GetStringTrackedDeviceProperty(deviceId, prop, result, capactiy, ref error);
00276                 return result.ToString();
00277             }
00278             return (error != ETrackedPropertyError.TrackedProp_Success) ? error.ToString() : "<unknown>";
00279         }
