00288         private static bool runningTemporarySession = false;
00289         public static bool InitializeTemporarySession(bool initInput = false)
00290         {
00291             if (Application.isEditor)
00292             {
00293                 //bool needsInit = (!active && !usingNativeSupport && !runningTemporarySession);
00294 
00295                 EVRInitError initError = EVRInitError.None;
00296                 OpenVR.GetGenericInterface(OpenVR.IVRCompositor_Version, ref initError);
00297                 bool needsInit = initError != EVRInitError.None;
00298 
00299                 if (needsInit)
00300                 {
00301                     EVRInitError error = EVRInitError.None;
00302                     OpenVR.Init(ref error, EVRApplicationType.VRApplication_Overlay);
00303 
00304                     if (error != EVRInitError.None)
00305                     {
00306                         Debug.LogError("<b>[SteamVR]</b> Error during OpenVR Init: " + error.ToString());
00307                         return false;
00308                     }
00309 
00310                     IdentifyEditorApplication(false);
00311 
00312                     SteamVR_Input.IdentifyActionsFile(false);
00313 
00314                     runningTemporarySession = true;
00315                 }
00316 
00317                 if (initInput)
00318                 {
00319                     SteamVR_Input.Initialize(true);
00320                 }
00321 
00322                 return needsInit;
00323             }
00324 
00325             return false;
00326         }
