00377         public static string GetResourcesFolderPath(bool fromAssetsDirectory = false)
00378         {
00379             SteamVR_Settings asset = ScriptableObject.CreateInstance<SteamVR_Settings>();
00380             UnityEditor.MonoScript scriptAsset = UnityEditor.MonoScript.FromScriptableObject(asset);
00381 
00382             string scriptPath = UnityEditor.AssetDatabase.GetAssetPath(scriptAsset);
00383 
00384             System.IO.FileInfo fi = new System.IO.FileInfo(scriptPath);
00385             string rootPath = fi.Directory.Parent.ToString();
00386 
00387             string resourcesPath = System.IO.Path.Combine(rootPath, "Resources");
00388 
00389             resourcesPath = resourcesPath.Replace("//", "/");
00390             resourcesPath = resourcesPath.Replace("\\\\", "\\");
00391             resourcesPath = resourcesPath.Replace("\\", "/");
00392 
00393             if (fromAssetsDirectory)
00394             {
00395                 int assetsIndex = resourcesPath.IndexOf("/Assets/");
00396                 resourcesPath = resourcesPath.Substring(assetsIndex + 1);
00397             }
00398 
00399             return resourcesPath;
00400         }
