00414         public static string GenerateCleanProductName()
00415         {
00416             string productName = Application.productName;
00417             if (string.IsNullOrEmpty(productName))
00418                 productName = "unnamed_product";
00419             else
00420             {
00421                 productName = System.Text.RegularExpressions.Regex.Replace(Application.productName, "[^\\w\\._]", "");
00422                 productName = productName.ToLower();
00423             }
00424 
00425             return productName;
