00521         private static void IdentifyEditorApplication(bool showLogs = true)
00522         {
00523             //bool isInstalled = OpenVR.Applications.IsApplicationInstalled(SteamVR_Settings.instance.editorAppKey);
00524 
00525             string manifestPath = GetManifestFile();
00526 
00527             EVRApplicationError addManifestErr = OpenVR.Applications.AddApplicationManifest(manifestPath, true);
00528             if (addManifestErr != EVRApplicationError.None)
00529                 Debug.LogError("<b>[SteamVR]</b> Error adding vr manifest file: " + addManifestErr.ToString());
00530             else
00531             {
00532                 if (showLogs)
00533                     Debug.Log("<b>[SteamVR]</b> Successfully added VR manifest to SteamVR");
00534             }
00535 
00536             int processId = System.Diagnostics.Process.GetCurrentProcess().Id;
00537             EVRApplicationError applicationIdentifyErr = OpenVR.Applications.IdentifyApplication((uint)processId, SteamVR_Settings.instance.editorAppKey);
00538 
00539             if (applicationIdentifyErr != EVRApplicationError.None)
00540                 Debug.LogError("<b>[SteamVR]</b> Error identifying application: " + applicationIdentifyErr.ToString());
00541             else
00542             {
00543                 if (showLogs)
00544                     Debug.Log(string.Format("<b>[SteamVR]</b> Successfully identified process as editor project to SteamVR ({0})", SteamVR_Settings.instance.editorAppKey));
00545             }
00546         }
