00570         private void OnNewPoses(TrackedDevicePose_t[] poses)
00571         {
00572             // Update eye offsets to account for IPD changes.
00573             eyes[0] = new SteamVR_Utils.RigidTransform(hmd.GetEyeToHeadTransform(EVREye.Eye_Left));
00574             eyes[1] = new SteamVR_Utils.RigidTransform(hmd.GetEyeToHeadTransform(EVREye.Eye_Right));
00575 
00576             for (int i = 0; i < poses.Length; i++)
00577             {
00578                 var connected = poses[i].bDeviceIsConnected;
00579                 if (connected != SteamVR.connected[i])
00580                 {
00581                     SteamVR_Events.DeviceConnected.Send(i, connected);
00582                 }
00583             }
00584 
00585             if (poses.Length > OpenVR.k_unTrackedDeviceIndex_Hmd)
00586             {
00587                 var result = poses[OpenVR.k_unTrackedDeviceIndex_Hmd].eTrackingResult;
00588 
00589                 var initializing = result == ETrackingResult.Uninitialized;
00590                 if (initializing != SteamVR.initializing)
00591                 {
00592                     SteamVR_Events.Initializing.Send(initializing);
00593                 }
00594 
00595                 var calibrating =
00596                     result == ETrackingResult.Calibrating_InProgress ||
00597                     result == ETrackingResult.Calibrating_OutOfRange;
00598                 if (calibrating != SteamVR.calibrating)
00599                 {
00600                     SteamVR_Events.Calibrating.Send(calibrating);
00601                 }
00602 
00603                 var outOfRange =
00604                     result == ETrackingResult.Running_OutOfRange ||
00605                     result == ETrackingResult.Calibrating_OutOfRange;
00606                 if (outOfRange != SteamVR.outOfRange)
00607                 {
00608                     SteamVR_Events.OutOfRange.Send(outOfRange);
00609                 }
00610             }
00611         }
