00615         private SteamVR()
00616         {
00617             hmd = OpenVR.System;
00618             Debug.Log("<b>[SteamVR]</b> Initialized. Connected to " + hmd_TrackingSystemName + ":" + hmd_SerialNumber);
00619 
00620             compositor = OpenVR.Compositor;
00621             overlay = OpenVR.Overlay;
00622 
00623             // Setup render values
00624             uint w = 0, h = 0;
00625             hmd.GetRecommendedRenderTargetSize(ref w, ref h);
00626             sceneWidth = (float)w;
00627             sceneHeight = (float)h;
00628 
00629             float l_left = 0.0f, l_right = 0.0f, l_top = 0.0f, l_bottom = 0.0f;
00630             hmd.GetProjectionRaw(EVREye.Eye_Left, ref l_left, ref l_right, ref l_top, ref l_bottom);
00631 
00632             float r_left = 0.0f, r_right = 0.0f, r_top = 0.0f, r_bottom = 0.0f;
00633             hmd.GetProjectionRaw(EVREye.Eye_Right, ref r_left, ref r_right, ref r_top, ref r_bottom);
00634 
00635             tanHalfFov = new Vector2(
00636                 Mathf.Max(-l_left, l_right, -r_left, r_right),
00637                 Mathf.Max(-l_top, l_bottom, -r_top, r_bottom));
00638 
00639             textureBounds = new VRTextureBounds_t[2];
00640 
00641             textureBounds[0].uMin = 0.5f + 0.5f * l_left / tanHalfFov.x;
00642             textureBounds[0].uMax = 0.5f + 0.5f * l_right / tanHalfFov.x;
00643             textureBounds[0].vMin = 0.5f - 0.5f * l_bottom / tanHalfFov.y;
00644             textureBounds[0].vMax = 0.5f - 0.5f * l_top / tanHalfFov.y;
00645 
00646             textureBounds[1].uMin = 0.5f + 0.5f * r_left / tanHalfFov.x;
00647             textureBounds[1].uMax = 0.5f + 0.5f * r_right / tanHalfFov.x;
00648             textureBounds[1].vMin = 0.5f - 0.5f * r_bottom / tanHalfFov.y;
00649             textureBounds[1].vMax = 0.5f - 0.5f * r_top / tanHalfFov.y;
00650 
00651             // Grow the recommended size to account for the overlapping fov
00652             sceneWidth = sceneWidth / Mathf.Max(textureBounds[0].uMax - textureBounds[0].uMin, textureBounds[1].uMax - textureBounds[1].uMin);
00653             sceneHeight = sceneHeight / Mathf.Max(textureBounds[0].vMax - textureBounds[0].vMin, textureBounds[1].vMax - textureBounds[1].vMin);
00654 
00655             aspect = tanHalfFov.x / tanHalfFov.y;
00656             fieldOfView = 2.0f * Mathf.Atan(tanHalfFov.y) * Mathf.Rad2Deg;
00657 
00658             eyes = new SteamVR_Utils.RigidTransform[] {
00659             new SteamVR_Utils.RigidTransform(hmd.GetEyeToHeadTransform(EVREye.Eye_Left)),
00660             new SteamVR_Utils.RigidTransform(hmd.GetEyeToHeadTransform(EVREye.Eye_Right)) };
00661 
00662             switch (SystemInfo.graphicsDeviceType)
00663             {
00664 #if (UNITY_5_4)
00665                 case UnityEngine.Rendering.GraphicsDeviceType.OpenGL2:
00666 #endif
00667                 case UnityEngine.Rendering.GraphicsDeviceType.OpenGLCore:
00668                 case UnityEngine.Rendering.GraphicsDeviceType.OpenGLES2:
00669                 case UnityEngine.Rendering.GraphicsDeviceType.OpenGLES3:
00670                     textureType = ETextureType.OpenGL;
00671                     break;
00672 #if !(UNITY_5_4)
00673             case UnityEngine.Rendering.GraphicsDeviceType.Vulkan:
00674                 textureType = ETextureType.Vulkan;
00675                 break;
00676 #endif
00677                 default:
00678                     textureType = ETextureType.DirectX;
00679                     break;
00680             }
00681 
00682             SteamVR_Events.Initializing.Listen(OnInitializing);
00683             SteamVR_Events.Calibrating.Listen(OnCalibrating);
00684             SteamVR_Events.OutOfRange.Listen(OnOutOfRange);
00685             SteamVR_Events.DeviceConnected.Listen(OnDeviceConnected);
00686             SteamVR_Events.NewPoses.Listen(OnNewPoses);
00687         }
00688 
00689         ~SteamVR()
00690         {
00691             Dispose(false);
00692         }
00693 
00694         public void Dispose()
00695         {
00696             Dispose(true);
00697             System.GC.SuppressFinalize(this);
00698         }
00699 
00700         private void Dispose(bool disposing)
00701         {
00702             SteamVR_Events.Initializing.Remove(OnInitializing);
00703             SteamVR_Events.Calibrating.Remove(OnCalibrating);
00704             SteamVR_Events.OutOfRange.Remove(OnOutOfRange);
00705             SteamVR_Events.DeviceConnected.Remove(OnDeviceConnected);
00706             SteamVR_Events.NewPoses.Remove(OnNewPoses);
00707 
00708             _instance = null;
00709         }
00710 
00711         // Use this interface to avoid accidentally creating the instance in the process of attempting to dispose of it.
00712         public static void SafeDispose()
00713         {
00714             if (_instance != null)
00715                 _instance.Dispose();
00716         }
