00131         {
00132             if (needsReinit && actionPath != null)
00133             {
00134                 SteamVR_Action existingAction = FindExistingActionForPartialPath(actionPath);
00135 
00136                 if (existingAction == null)
00137                 {
00138                     this.sourceMap = null;
00139                 }
00140                 else
00141                 { 
00142                     this.actionPath = existingAction.fullPath;
00143                     this.sourceMap = (SourceMap)existingAction.GetSourceMap();
00144 
00145                     initialized = true;
00146                     needsReinit = false;
00147                     return actionPath;
00148                 }
00149             }
00150 
00151             return null;
00152         }
