00487         public static bool operator ==(SteamVR_Action action1, SteamVR_Action action2)
00488         {
00489             bool action1null = (ReferenceEquals(null, action1) || string.IsNullOrEmpty(action1.actionPath) || action1.GetSourceMap() == null);
00490             bool action2null = (ReferenceEquals(null, action2) || string.IsNullOrEmpty(action2.actionPath) || action2.GetSourceMap() == null);
00491 
00492             if (action1null && action2null)
00493                 return true;
00494             else if (action1null != action2null)
00495                 return false;
00496 
00497             return action1.Equals(action2);
00498         }
