00503         public static SteamVR_Action FindExistingActionForPartialPath(string path)
00504         {
00505             if (string.IsNullOrEmpty(path) || path.IndexOf('/') == -1)
00506                 return null;
00507 
00508             //   0    1       2     3   4
00509             //    /actions/default/in/foobar
00510             string[] pathParts = path.Split('/');
00511             SteamVR_Action existingAction;
00512 
00513             if (pathParts.Length >= 2 && string.IsNullOrEmpty(pathParts[2]))
00514             {
00515                 string set = pathParts[2];
00516                 string name = pathParts[4];
00517                 existingAction = SteamVR_Input.GetBaseAction(set, name);
00518             }
00519             else
00520             {
00521                 existingAction = SteamVR_Input.GetBaseActionFromPath(path);
00522             }
00523 
00524             return existingAction;
00525         }
