00543     public abstract class SteamVR_Action_Source_Map<SourceElement> : SteamVR_Action_Source_Map where SourceElement : SteamVR_Action_Source, new()
00544     {
00549         public SourceElement this[SteamVR_Input_Sources inputSource]
00550         {
00551             get
00552             {
00553                 return GetSourceElementForIndexer(inputSource);
00554             }
00555         }
00556 
00557         protected virtual void OnAccessSource(SteamVR_Input_Sources inputSource) { }
00558 
00559         protected Dictionary<SteamVR_Input_Sources, SourceElement> sources = new Dictionary<SteamVR_Input_Sources, SourceElement>(new SteamVR_Input_Sources_Comparer());
00560 
00564         public override void Initialize()
00565         {
00566             base.Initialize();
00567 
00568             var sourceEnumerator = sources.GetEnumerator();
00569             while (sourceEnumerator.MoveNext())
00570             {
00571                 var sourceElement = sourceEnumerator.Current;
00572                 sourceElement.Value.Initialize();
00573             }
00574         }
00575 
00576         protected override void PreinitializeMap(SteamVR_Input_Sources inputSource, SteamVR_Action wrappingAction)
00577         {
00578             sources.Add(inputSource, new SourceElement());
00579             sources[inputSource].Preinitialize(wrappingAction, inputSource);
00580         }
00581 
00582         // Normally I'd just make the indexer virtual and override that but some unity versions don't like that
00583         protected virtual SourceElement GetSourceElementForIndexer(SteamVR_Input_Sources inputSource)
00584         {
00585             OnAccessSource(inputSource);
00586             return sources[inputSource];
00587         }
00588     }
