00590     public abstract class SteamVR_Action_Source_Map
00591     {
00593         public string fullPath { get; protected set; }
00594 
00596         public ulong handle { get; protected set; }
00597 
00599         public SteamVR_ActionSet actionSet { get; protected set; }
00600 
00602         public SteamVR_ActionDirections direction { get; protected set; }
00603 
00605         public SteamVR_Action action;
00606 
00607         public virtual void PreInitialize(SteamVR_Action wrappingAction, string actionPath, bool throwErrors = true)
00608         {
00609             fullPath = actionPath;
00610             action = wrappingAction;
00611 
00612             actionSet = SteamVR_Input.GetActionSetFromPath(GetActionSetPath());
00613 
00614             direction = GetActionDirection();
00615 
00616             SteamVR_Input_Sources[] sources = SteamVR_Input_Source.GetAllSources();
00617             for (int sourceIndex = 0; sourceIndex < sources.Length; sourceIndex++)
00618             {
00619                 PreinitializeMap(sources[sourceIndex], wrappingAction);
00620             }
00621         }
00622 
00626         protected abstract void PreinitializeMap(SteamVR_Input_Sources inputSource, SteamVR_Action wrappingAction);
00627 
00631         public virtual void Initialize()
00632         {
00633             ulong newHandle = 0;
00634             EVRInputError err = OpenVR.Input.GetActionHandle(fullPath.ToLower(), ref newHandle);
00635             handle = newHandle;
00636 
00637             if (err != EVRInputError.None)
00638                 Debug.LogError("<b>[SteamVR]</b> GetActionHandle (" + fullPath.ToLower() + ") error: " + err.ToString());
00639         }
00640 
00641         private string GetActionSetPath()
00642         {
00643             int actionsEndIndex = fullPath.IndexOf('/', 1);
00644             int setStartIndex = actionsEndIndex + 1;
00645             int setEndIndex = fullPath.IndexOf('/', setStartIndex);
00646             int count = setEndIndex;
00647 
00648             return fullPath.Substring(0, count);
00649         }
00650 
00651         private SteamVR_ActionDirections GetActionDirection()
00652         {
00653             int actionsEndIndex = fullPath.IndexOf('/', 1);
00654             int setStartIndex = actionsEndIndex + 1;
00655             int setEndIndex = fullPath.IndexOf('/', setStartIndex);
00656             int directionEndIndex = fullPath.IndexOf('/', setEndIndex + 1);
00657             int count = directionEndIndex - setEndIndex - 1;
00658             string direction = fullPath.Substring(setEndIndex + 1, count);
00659 
00660             if (direction == "in")
00661                 return SteamVR_ActionDirections.In;
00662             else if (direction == "out")
00663                 return SteamVR_ActionDirections.Out;
00664             else
00665                 Debug.LogError("Could not find match for direction: " + direction);
00666             return SteamVR_ActionDirections.In;
00667         }
00668     }
