00278 
00281         public bool lastStateUp { get { return lastActionData.bState == false && lastActionData.bChanged; } }
00282 
00285         public override bool lastChanged { get { return lastActionData.bChanged; } protected set { } }
00286 
00288         public override ulong activeOrigin
00289         {
00290             get
