00292                 if (active)
00293                     return actionData.activeOrigin;
00294 
00295                 return 0;
00296             }
00297         }
00298 
00300         public override ulong lastActiveOrigin { get { return lastActionData.activeOrigin; } }
00301 
00303         public override bool active { get { return activeBinding && action.actionSet.IsActive(inputSource); } }
