00334         public override void Initialize()
00335         {
00336             base.Initialize();
00337 
00338             if (actionData_size == 0)
00339                 actionData_size = (uint)Marshal.SizeOf(typeof(InputDigitalActionData_t));
00340 
00341         }
00342 
00346         public override void UpdateValue()
00347         {
00348             lastActionData = actionData;
00349             lastActive = active;
00350 
00351             EVRInputError err = OpenVR.Input.GetDigitalActionData(action.handle, ref actionData, actionData_size, inputSourceHandle);
00352             if (err != EVRInputError.None)
00353                 Debug.LogError("<b>[SteamVR]</b> GetDigitalActionData error (" + action.fullPath + "): " + err.ToString() + " handle: " + action.handle.ToString());
00354 
00355             if (changed)
00356                 changedTime = Time.realtimeSinceStartup + actionData.fUpdateTime;
00357 
00358             updateTime = Time.realtimeSinceStartup;
00359 
00360             if (active)
00361             {
00362                 if (onStateDown != null && stateDown)
00363                     onStateDown.Invoke(booleanAction, inputSource);
00364 
00365                 if (onStateUp != null && stateUp)
00366                     onStateUp.Invoke(booleanAction, inputSource);
00367 
00368                 if (onState != null && state)
00369                     onState.Invoke(booleanAction, inputSource);
00370 
00371                 if (onChange != null && changed)
00372                     onChange.Invoke(booleanAction, inputSource, state);
00373 
00374                 if (onUpdate != null)
00375                     onUpdate.Invoke(booleanAction, inputSource, state);
00376             }
00377 
00378             if (onActiveBindingChange != null && lastActiveBinding != activeBinding)
00379                 onActiveBindingChange.Invoke(booleanAction, inputSource, activeBinding);
00380 
00381             if (onActiveChange != null && lastActive != active)
00382                 onActiveChange.Invoke(booleanAction, inputSource, activeBinding);
00383         }
