00154     public class SteamVR_Action_In_Source_Map<SourceElement> : SteamVR_Action_Source_Map<SourceElement>
00155         where SourceElement : SteamVR_Action_In_Source, new()
00156     {
00157         protected List<SteamVR_Input_Sources> updatingSources = new List<SteamVR_Input_Sources>();
00158 
00165         public bool IsUpdating(SteamVR_Input_Sources inputSource)
00166         {
00167             for (int sourceIndex = 0; sourceIndex < updatingSources.Count; sourceIndex++)
00168             {
00169                 if (inputSource == updatingSources[sourceIndex])
00170                     return true;
00171             }
00172 
00173             return false;
00174         }
00175 
00176         protected override void OnAccessSource(SteamVR_Input_Sources inputSource)
00177         {
00178             if (SteamVR_Action.startUpdatingSourceOnAccess)
00179             {
00180                 ForceAddSourceToUpdateList(inputSource);
00181             }
00182         }
00183 
00190         public void ForceAddSourceToUpdateList(SteamVR_Input_Sources inputSource)
00191         {
00192             if (sources[inputSource].isUpdating == false)
00193             {
00194                 updatingSources.Add(inputSource);
00195                 sources[inputSource].isUpdating = true;
00196 
00197                 if (SteamVR_Input.isStartupFrame == false)
00198                     sources[inputSource].UpdateValue();
00199             }
00200         }
00201 
00206         public void UpdateValues()
00207         {
00208             for (int sourceIndex = 0; sourceIndex < updatingSources.Count; sourceIndex++)
00209             {
00210                 sources[updatingSources[sourceIndex]].UpdateValue();
00211             }
00212         }
00213     }
