00219     public abstract class SteamVR_Action_In_Source : SteamVR_Action_Source, ISteamVR_Action_In_Source
00220     {
00221         protected static uint inputOriginInfo_size = 0;
00222 
00228         public bool isUpdating { get; set; }
00229         
00231         public float updateTime { get; protected set; }
00232 
00234         public abstract ulong activeOrigin { get; }
00235 
00237         public abstract ulong lastActiveOrigin { get; }
00238 
00240         public abstract bool changed { get; protected set; }
00241 
00243         public abstract bool lastChanged { get; protected set; }
00244 
00246         public SteamVR_Input_Sources activeDevice { get { UpdateOriginTrackedDeviceInfo();  return SteamVR_Input_Source.GetSource(inputOriginInfo.devicePath); } }
00247 
00249         public uint trackedDeviceIndex { get { UpdateOriginTrackedDeviceInfo(); return inputOriginInfo.trackedDeviceIndex; } }
00250 
00252         public string renderModelComponentName { get { UpdateOriginTrackedDeviceInfo(); return inputOriginInfo.rchRenderModelComponentName; } }
00253 
00257         public string localizedOriginName { get { UpdateOriginTrackedDeviceInfo(); return GetLocalizedOrigin(); } }
00258 
00259 
00261         public float changedTime { get; protected set; }
00262 
00263         protected int lastOriginGetFrame { get; set; }
00264 
00265         protected InputOriginInfo_t inputOriginInfo = new InputOriginInfo_t();
00266         protected InputOriginInfo_t lastInputOriginInfo = new InputOriginInfo_t();
00267 
00269         public abstract void UpdateValue();
00270 
00274         public override void Initialize()
00275         {
00276             base.Initialize();
00277 
00278             if (inputOriginInfo_size == 0)
00279                 inputOriginInfo_size = (uint)Marshal.SizeOf(typeof(InputOriginInfo_t));
00280         }
00281 
00282         protected void UpdateOriginTrackedDeviceInfo()
00283         {
00284             if (lastOriginGetFrame != Time.frameCount) //only get once per frame
00285             {
00286                 EVRInputError err = OpenVR.Input.GetOriginTrackedDeviceInfo(activeOrigin, ref inputOriginInfo, inputOriginInfo_size);
00287 
00288                 if (err != EVRInputError.None)
00289                     Debug.LogError("<b>[SteamVR]</b> GetOriginTrackedDeviceInfo error (" + fullPath + "): " + err.ToString() + " handle: " + handle.ToString() + " activeOrigin: " + activeOrigin.ToString() + " active: " + active);
00290 
00291                 lastInputOriginInfo = inputOriginInfo;
00292                 lastOriginGetFrame = Time.frameCount;
00293             }
00294         }
00295 
00308         public string GetLocalizedOriginPart(params EVRInputStringBits[] localizedParts)
00309         {
00310             UpdateOriginTrackedDeviceInfo();
00311 
00312             if (active)
00313             {
00314                 return SteamVR_Input.GetLocalizedName(activeOrigin, localizedParts);
00315             }
00316 
00317             return null;
00318         }
00319 
00323         public string GetLocalizedOrigin()
00324         {
00325             UpdateOriginTrackedDeviceInfo();
00326 
00327             if (active)
00328             {
00329                 return SteamVR_Input.GetLocalizedName(activeOrigin, EVRInputStringBits.VRInputString_All);
00330             }
00331 
00332             return null;
00333         }
00334     }
