00383     public class SteamVR_Action_Pose_Source_Map<Source> : SteamVR_Action_In_Source_Map<Source>
00384         where Source : SteamVR_Action_Pose_Source, new()
00385     {
00389         public void SetTrackingUniverseOrigin(ETrackingUniverseOrigin newOrigin)
00390         {
00391             var sourceEnumerator = sources.GetEnumerator();
00392             while (sourceEnumerator.MoveNext())
00393             {
00394                 var sourceElement = sourceEnumerator.Current;
00395                 sourceElement.Value.universeOrigin = newOrigin;
00396             }
00397         }
00398 
00399         public virtual void UpdateValues(bool skipStateAndEventUpdates)
00400         {
00401             for (int sourceIndex = 0; sourceIndex < updatingSources.Count; sourceIndex++)
00402             {
00403                 sources[updatingSources[sourceIndex]].UpdateValue(skipStateAndEventUpdates);
00404             }
00405         }
