00562         public virtual void UpdateValue(bool skipStateAndEventUpdates)
00563         {
00564             lastChanged = changed;
00565             lastPoseActionData = poseActionData;
00566             lastLocalPosition = localPosition;
00567             lastLocalRotation = localRotation;
00568             lastVelocity = velocity;
00569             lastAngularVelocity = angularVelocity;
00570 
00571             EVRInputError err = OpenVR.Input.GetPoseActionData(handle, universeOrigin, predictedSecondsFromNow, ref poseActionData, poseActionData_size, inputSourceHandle);
00572             if (err != EVRInputError.None)
00573             {
00574                 Debug.LogError("<b>[SteamVR]</b> GetPoseActionData error (" + fullPath + "): " + err.ToString() + " Handle: " + handle.ToString() + ". Input source: " + inputSource.ToString());
00575             }
00576 
00577             SetCacheVariables();
00578             changed = GetChanged();
00579 
00580             if (changed)
00581                 changedTime = updateTime + predictedSecondsFromNow;
00582 
00583             if (skipStateAndEventUpdates == false)
00584                 CheckAndSendEvents();
00585         }
00586 
00587         protected void SetCacheVariables()
00588         {
00589             localPosition = SteamVR_Utils.GetPosition(poseActionData.pose.mDeviceToAbsoluteTracking);
00590             localRotation = SteamVR_Utils.GetRotation(poseActionData.pose.mDeviceToAbsoluteTracking);
00591             velocity = GetUnityCoordinateVelocity(poseActionData.pose.vVelocity);
00592             angularVelocity = GetUnityCoordinateAngularVelocity(poseActionData.pose.vAngularVelocity);
00593             updateTime = Time.realtimeSinceStartup;
00594         }
00595 
00596         protected bool GetChanged()
00597         {
00598             if (Vector3.Distance(localPosition, lastLocalPosition) > changeTolerance)
00599                 return true;
00600             else if (Mathf.Abs(Quaternion.Angle(localRotation, lastLocalRotation)) > changeTolerance)
00601                 return true;
00602             else if (Vector3.Distance(velocity, lastVelocity) > changeTolerance)
00603                 return true;
00604             else if (Vector3.Distance(angularVelocity, lastAngularVelocity) > changeTolerance)
00605                 return true;
00606 
00607             return false;
00608         }
00609 
00615         public bool GetVelocitiesAtTimeOffset(float secondsFromNow, out Vector3 velocityAtTime, out Vector3 angularVelocityAtTime)
00616         {
00617             EVRInputError err = OpenVR.Input.GetPoseActionData(handle, universeOrigin, secondsFromNow, ref tempPoseActionData, poseActionData_size, inputSourceHandle);
00618             if (err != EVRInputError.None)
00619             {
00620                 Debug.LogError("<b>[SteamVR]</b> GetPoseActionData error (" + fullPath + "): " + err.ToString() + " handle: " + handle.ToString()); //todo: this should be an error
00621 
00622                 velocityAtTime = Vector3.zero;
00623                 angularVelocityAtTime = Vector3.zero;
00624                 return false;
00625             }
00626 
00627             velocityAtTime = GetUnityCoordinateVelocity(tempPoseActionData.pose.vVelocity);
00628             angularVelocityAtTime = GetUnityCoordinateAngularVelocity(tempPoseActionData.pose.vAngularVelocity);
00629 
00630             return true;
00631         }
00632 
00638         public bool GetPoseAtTimeOffset(float secondsFromNow, out Vector3 positionAtTime, out Quaternion rotationAtTime, out Vector3 velocityAtTime, out Vector3 angularVelocityAtTime)
00639         {
00640             EVRInputError err = OpenVR.Input.GetPoseActionData(handle, universeOrigin, secondsFromNow, ref tempPoseActionData, poseActionData_size, inputSourceHandle);
00641             if (err != EVRInputError.None)
00642             {
00643                 Debug.LogError("<b>[SteamVR]</b> GetPoseActionData error (" + fullPath + "): " + err.ToString() + " handle: " + handle.ToString()); //todo: this should be an error
00644 
00645                 velocityAtTime = Vector3.zero;
00646                 angularVelocityAtTime = Vector3.zero;
00647                 positionAtTime = Vector3.zero;
00648                 rotationAtTime = Quaternion.identity;
00649                 return false;
00650             }
00651 
00652             velocityAtTime = GetUnityCoordinateVelocity(tempPoseActionData.pose.vVelocity);
00653             angularVelocityAtTime = GetUnityCoordinateAngularVelocity(tempPoseActionData.pose.vAngularVelocity);
00654             positionAtTime = SteamVR_Utils.GetPosition(tempPoseActionData.pose.mDeviceToAbsoluteTracking);
00655             rotationAtTime = SteamVR_Utils.GetRotation(tempPoseActionData.pose.mDeviceToAbsoluteTracking);
00656 
00657             return true;
00658         }
00659 
00664         public void UpdateTransform(Transform transformToUpdate)
00665         {
00666             transformToUpdate.localPosition = localPosition;
00667             transformToUpdate.localRotation = localRotation;
00668         }
00669         
00670         protected virtual void CheckAndSendEvents()
00671         {
00672             if (trackingState != lastTrackingState && onTrackingChanged != null)
00673                 onTrackingChanged.Invoke(poseAction, inputSource, trackingState);
00674 
00675             if (poseIsValid != lastPoseIsValid && onValidPoseChanged != null)
00676                 onValidPoseChanged.Invoke(poseAction, inputSource, poseIsValid);
00677 
00678             if (deviceIsConnected != lastDeviceIsConnected && onDeviceConnectedChanged != null)
00679                 onDeviceConnectedChanged.Invoke(poseAction, inputSource, deviceIsConnected);
00680 
00681             if (changed && onChange != null)
00682                 onChange.Invoke(poseAction, inputSource);
00683 
00684             if (active != lastActive && onActiveChange != null)
00685                 onActiveChange.Invoke(poseAction, inputSource, active);
00686 
00687             if (activeBinding != lastActiveBinding && onActiveBindingChange != null)
00688                 onActiveBindingChange.Invoke(poseAction, inputSource, activeBinding);
00689 
00690             if (onUpdate != null)
00691                 onUpdate.Invoke(poseAction, inputSource);
00692         }
