00702         }
00703 
00704         protected Vector3 GetUnityCoordinateVelocity(float x, float y, float z)
00705         {
00706             Vector3 vector = new Vector3();
00707             vector.x = x;
00708             vector.y = y;
00709             vector.z = -z;
00710             return vector;
00711         }
00712 
00713         protected Vector3 GetUnityCoordinateAngularVelocity(float x, float y, float z)
00714         {
00715             Vector3 vector = new Vector3();
00716             vector.x = -x;
00717             vector.y = -y;
00718             vector.z = z;
00719             return vector;
00720         }
