00278         public override void Initialize()
00279         {
00280             base.Initialize();
00281 
00282             if (actionData_size == 0)
00283                 actionData_size = (uint)Marshal.SizeOf(typeof(InputAnalogActionData_t));
00284         }
00285 
00289         public override void UpdateValue()
00290         {
00291             lastActionData = actionData;
00292             lastActive = active;
00293 
00294             EVRInputError err = OpenVR.Input.GetAnalogActionData(handle, ref actionData, actionData_size, SteamVR_Input_Source.GetHandle(inputSource));
00295             if (err != EVRInputError.None)
00296                 Debug.LogError("<b>[SteamVR]</b> GetAnalogActionData error (" + fullPath + "): " + err.ToString() + " handle: " + handle.ToString());
00297             
00298             updateTime = Time.realtimeSinceStartup;
00299 
00300             changed = false;
00301 
00302             if (active)
00303             {
00304                 if (delta > changeTolerance || delta < -changeTolerance)
00305                 {
00306                     changed = true;
00307                     changedTime = Time.realtimeSinceStartup + actionData.fUpdateTime; //fUpdateTime is the time from the time the action was called that the action changed
00308 
00309                     if (onChange != null)
00310                         onChange.Invoke(singleAction, inputSource, axis, delta);
00311                 }
00312 
00313                 if (axis != 0)
00314                 {
00315                     if (onAxis != null)
00316                         onAxis.Invoke(singleAction, inputSource, axis, delta);
00317                 }
00318 
00319                 if (onUpdate != null)
00320                 {
00321                     onUpdate.Invoke(singleAction, inputSource, axis, delta);
00322                 }
00323             }
00324 
00325 
00326             if (onActiveBindingChange != null && lastActiveBinding != activeBinding)
00327                 onActiveBindingChange.Invoke(singleAction, inputSource, activeBinding);
00328 
00329             if (onActiveChange != null && lastActive != active)
00330                 onActiveChange.Invoke(singleAction, inputSource, activeBinding);
00331         }
