00846             boneRotations = new Quaternion[SteamVR_Action_Skeleton.numBones];
00847             lastBoneRotations = new Quaternion[SteamVR_Action_Skeleton.numBones];
00848 
00849             rangeOfMotion = EVRSkeletalMotionRange.WithController;
00850             skeletalTransformSpace = EVRSkeletalTransformSpace.Parent;
00851 
00852             fingerCurls = new float[SteamVR_Skeleton_FingerIndexes.enumArray.Length];
00853             fingerSplays = new float[SteamVR_Skeleton_FingerSplayIndexes.enumArray.Length];
00854 
00855             lastFingerCurls = new float[SteamVR_Skeleton_FingerIndexes.enumArray.Length];
00856             lastFingerSplays = new float[SteamVR_Skeleton_FingerSplayIndexes.enumArray.Length];
00857         }
00858 
00863         public override void Initialize()
00864         {
00865             base.Initialize();
00866 
00867             if (skeletonActionData_size == 0)
00868                 skeletonActionData_size = (uint)Marshal.SizeOf(typeof(InputSkeletalActionData_t));
00869         }
00870 
00874         public override void UpdateValue()
00875         {
00876             UpdateValue(false);
00877         }
00878 
00882         public override void UpdateValue(bool skipStateAndEventUpdates)
00883         {
00884             lastActive = active;
00885             lastSkeletonActionData = skeletonActionData;
00886             lastSkeletalSummaryData = skeletalSummaryData;
00887 
00888             for (int boneIndex = 0; boneIndex < SteamVR_Action_Skeleton.numBones; boneIndex++)
00889             {
00890                 lastBonePositions[boneIndex] = bonePositions[boneIndex];
00891                 lastBoneRotations[boneIndex] = boneRotations[boneIndex];
00892             }
00893 
00894             for (int fingerIndex = 0; fingerIndex < SteamVR_Skeleton_FingerIndexes.enumArray.Length; fingerIndex++)
00895             {
00896                 lastFingerCurls[fingerIndex] = fingerCurls[fingerIndex];
00897             }
00898 
00899             for (int fingerIndex = 0; fingerIndex < SteamVR_Skeleton_FingerSplayIndexes.enumArray.Length; fingerIndex++)
00900             {
00901                 lastFingerSplays[fingerIndex] = fingerSplays[fingerIndex];
00902             }
00903 
00904             base.UpdateValue(true);
00905             poseChanged = changed;
00906 
00907             EVRInputError error = OpenVR.Input.GetSkeletalActionData(handle, ref skeletonActionData, skeletonActionData_size);
00908             if (error != EVRInputError.None)
00909             {
00910                 Debug.LogError("<b>[SteamVR]</b> GetSkeletalActionData error (" + fullPath + "): " + error.ToString() + " handle: " + handle.ToString());
00911                 return;
00912             }
00913 
00914             if (active)
00915             {
00916                 error = OpenVR.Input.GetSkeletalBoneData(handle, skeletalTransformSpace, rangeOfMotion, tempBoneTransforms);
00917                 if (error != EVRInputError.None)
00918                     Debug.LogError("<b>[SteamVR]</b> GetSkeletalBoneData error (" + fullPath + "): " + error.ToString() + " handle: " + handle.ToString());
00919 
00920                 GetSkeletalSummaryData(true);
00921 
00922                 for (int boneIndex = 0; boneIndex < tempBoneTransforms.Length; boneIndex++)
00923                 {
00924                     // SteamVR's coordinate system is right handed, and Unity's is left handed.  The FBX data has its
00925                     // X axis flipped when Unity imports it, so here we need to flip the X axis as well
00926                     bonePositions[boneIndex].x = -tempBoneTransforms[boneIndex].position.v0;
00927                     bonePositions[boneIndex].y = tempBoneTransforms[boneIndex].position.v1;
00928                     bonePositions[boneIndex].z = tempBoneTransforms[boneIndex].position.v2;
00929 
00930                     boneRotations[boneIndex].x = tempBoneTransforms[boneIndex].orientation.x;
00931                     boneRotations[boneIndex].y = -tempBoneTransforms[boneIndex].orientation.y;
00932                     boneRotations[boneIndex].z = -tempBoneTransforms[boneIndex].orientation.z;
00933                     boneRotations[boneIndex].w = tempBoneTransforms[boneIndex].orientation.w;
00934                 }
00935 
00936                 // Now that we're in the same handedness as Unity, rotate the root bone around the Y axis
00937                 // so that forward is facing down +Z
00938 
00939                 boneRotations[0] = SteamVR_Action_Skeleton.steamVRFixUpRotation * boneRotations[0];
00940             }
00941 
00942             if (changed == false)
00943             {
00944                 for (int boneIndex = 0; boneIndex < tempBoneTransforms.Length; boneIndex++)
00945                 {
00946                     if (Vector3.Distance(lastBonePositions[boneIndex], bonePositions[boneIndex]) > changeTolerance)
00947                     {
00948                         changed = true;
00949                         break;
00950                     }
00951 
00952                     if (Mathf.Abs(Quaternion.Angle(lastBoneRotations[boneIndex], boneRotations[boneIndex])) > changeTolerance)
00953                     {
00954                         changed = true;
00955                         break;
00956                     }
00957                 }
00958             }
00959 
00960             if (changed)
00961                 changedTime = Time.realtimeSinceStartup;
00962 
00963             if (skipStateAndEventUpdates == false)
00964                 CheckAndSendEvents();
00965         }
