01024         public SteamVR_Utils.RigidTransform[] GetReferenceTransforms(EVRSkeletalTransformSpace transformSpace, EVRSkeletalReferencePose referencePose)
01025         {
01026             SteamVR_Utils.RigidTransform[] transforms = new SteamVR_Utils.RigidTransform[GetBoneCount()];
01027 
01028             VRBoneTransform_t[] boneTransforms = new VRBoneTransform_t[transforms.Length];
01029 
01030             EVRInputError error = OpenVR.Input.GetSkeletalReferenceTransforms(handle, transformSpace, referencePose, boneTransforms);
01031             if (error != EVRInputError.None)
01032                 Debug.LogError("<b>[SteamVR]</b> GetSkeletalReferenceTransforms error (" + fullPath + "): " + error.ToString() + " handle: " + handle.ToString());
01033 
01034             for (int transformIndex = 0; transformIndex < boneTransforms.Length; transformIndex++)
01035             {
01036                 Vector3 position = new Vector3(-boneTransforms[transformIndex].position.v0, boneTransforms[transformIndex].position.v1, boneTransforms[transformIndex].position.v2);
01037                 Quaternion rotation = new Quaternion(boneTransforms[transformIndex].orientation.x, -boneTransforms[transformIndex].orientation.y, -boneTransforms[transformIndex].orientation.z, boneTransforms[transformIndex].orientation.w);
01038                 transforms[transformIndex] = new SteamVR_Utils.RigidTransform(position, rotation);
01039             }
01040 
01041             if (transforms.Length > 0)
01042             {
01043                 // Now that we're in the same handedness as Unity, rotate the root bone around the Y axis
01044                 // so that forward is facing down +Z
01045                 Quaternion qFixUpRot = Quaternion.AngleAxis(Mathf.PI * Mathf.Rad2Deg, Vector3.up);
01046 
01047                 transforms[0].rot = qFixUpRot * transforms[0].rot;
01048             }
01049 
01050             return transforms;
01051         }
