01083         protected VRSkeletalSummaryData_t GetSkeletalSummaryData(bool force = false)
01084         {
01085             if (force)
01086             {
01087                 EVRInputError error = OpenVR.Input.GetSkeletalSummaryData(handle, ref skeletalSummaryData);
01088                 if (error != EVRInputError.None)
01089                     Debug.LogError("<b>[SteamVR]</b> GetSkeletalSummaryData error (" + fullPath + "): " + error.ToString() + " handle: " + handle.ToString());
01090 
01091                 fingerCurls[0] = skeletalSummaryData.flFingerCurl0;
01092                 fingerCurls[1] = skeletalSummaryData.flFingerCurl1;
01093                 fingerCurls[2] = skeletalSummaryData.flFingerCurl2;
01094                 fingerCurls[3] = skeletalSummaryData.flFingerCurl3;
01095                 fingerCurls[4] = skeletalSummaryData.flFingerCurl4;
01096 
01097                 //no splay data for thumb
01098                 fingerSplays[0] = skeletalSummaryData.flFingerSplay0;
01099                 fingerSplays[1] = skeletalSummaryData.flFingerSplay1;
01100                 fingerSplays[2] = skeletalSummaryData.flFingerSplay2;
01101                 fingerSplays[3] = skeletalSummaryData.flFingerSplay3;
01102             }
01103 
01104             return skeletalSummaryData;
01105         }
01106 
01107         protected override void CheckAndSendEvents()
01108         {
01109             if (trackingState != lastTrackingState && onTrackingChanged != null)
01110                 onTrackingChanged.Invoke(skeletonAction, trackingState);
01111 
01112             if (poseIsValid != lastPoseIsValid && onValidPoseChanged != null)
01113                 onValidPoseChanged.Invoke(skeletonAction, poseIsValid);
01114 
01115             if (deviceIsConnected != lastDeviceIsConnected && onDeviceConnectedChanged != null)
01116                 onDeviceConnectedChanged.Invoke(skeletonAction, deviceIsConnected);
01117 
01118             if (changed && onChange != null)
01119                 onChange.Invoke(skeletonAction);
01120 
01121             if (active != lastActive && onActiveChange != null)
01122                 onActiveChange.Invoke(skeletonAction, active);
01123 
01124             if (activeBinding != lastActiveBinding && onActiveBindingChange != null)
01125                 onActiveBindingChange.Invoke(skeletonAction, activeBinding);
01126 
01127             if (onUpdate != null)
01128                 onUpdate.Invoke(skeletonAction);
01129         }
01130     }
