01291                 case thumbMetacarpal:
01292                 case thumbMiddle:
01293                 case thumbDistal:
01294                 case thumbTip:
01295                 case thumbAux:
01296                     return 0;
01297 
01298                 case indexMetacarpal:
01299                 case indexProximal:
01300                 case indexMiddle:
01301                 case indexDistal:
01302                 case indexTip:
01303                 case indexAux:
01304                     return 1;
01305 
01306                 case middleMetacarpal:
01307                 case middleProximal:
01308                 case middleMiddle:
01309                 case middleDistal:
01310                 case middleTip:
01311                 case middleAux:
01312                     return 2;
01313 
01314                 case ringMetacarpal:
01315                 case ringProximal:
01316                 case ringMiddle:
01317                 case ringDistal:
01318                 case ringTip:
01319                 case ringAux:
01320                     return 3;
01321 
01322                 case pinkyMetacarpal:
01323                 case pinkyProximal:
01324                 case pinkyMiddle:
01325                 case pinkyDistal:
01326                 case pinkyTip:
01327                 case pinkyAux:
01328                     return 4;
01329 
01330                 default:
01331                     return -1;
01332             }
