00274         public override void Preinitialize(SteamVR_Action wrappingAction, SteamVR_Input_Sources forInputSource)
00275         {
00276             base.Preinitialize(wrappingAction, forInputSource);
00277             vector2Action = (SteamVR_Action_Vector2)wrappingAction;
00278         }
00279 
00284         public override void Initialize()
00285         {
00286             base.Initialize();
00287 
00288             if (actionData_size == 0)
00289                 actionData_size = (uint)Marshal.SizeOf(typeof(InputAnalogActionData_t));
00290         }
00291 
00295         public override void UpdateValue()
00296         {
00297             lastActionData = actionData;
00298             lastActive = active;
00299             lastAxis = axis;
00300             lastDelta = delta;
00301 
00302             EVRInputError err = OpenVR.Input.GetAnalogActionData(handle, ref actionData, actionData_size, SteamVR_Input_Source.GetHandle(inputSource));
00303             if (err != EVRInputError.None)
00304                 Debug.LogError("<b>[SteamVR]</b> GetAnalogActionData error (" + fullPath + "): " + err.ToString() + " handle: " + handle.ToString());
00305 
00306             updateTime = Time.realtimeSinceStartup;
00307             axis = new Vector2(actionData.x, actionData.y);
00308             delta = new Vector2(actionData.deltaX, actionData.deltaY);
00309 
00310             changed = false;
00311 
00312             if (active)
00313             {
00314                 if (delta.magnitude > changeTolerance)
00315                 {
00316                     changed = true;
00317                     changedTime = Time.realtimeSinceStartup + actionData.fUpdateTime; //fUpdateTime is the time from the time the action was called that the action changed
00318 
00319                     if (onChange != null)
00320                         onChange.Invoke(vector2Action, inputSource, axis, delta);
00321                 }
00322 
00323                 if (axis != Vector2.zero)
00324                 {
00325                     if (onAxis != null)
00326                         onAxis.Invoke(vector2Action, inputSource, axis, delta);
00327                 }
00328 
00329                 if (onUpdate != null)
00330                 {
00331                     onUpdate.Invoke(vector2Action, inputSource, axis, delta);
00332                 }
00333             }
00334 
00335 
00336             if (onActiveBindingChange != null && lastActiveBinding != activeBinding)
00337                 onActiveBindingChange.Invoke(vector2Action, inputSource, activeBinding);
00338 
00339             if (onActiveChange != null && lastActive != active)
00340                 onActiveChange.Invoke(vector2Action, inputSource, activeBinding);
00341         }
