00188     public class SteamVR_Action_Vector3_Source : SteamVR_Action_In_Source, ISteamVR_Action_Vector3
00189     {
00190         protected static uint actionData_size = 0;
00191 
00193         public float changeTolerance = Mathf.Epsilon;
