00234         public override ulong activeOrigin
00235         {
00236             get
00237             {
00238                 if (active)
00239                     return actionData.activeOrigin;
00240 
00241                 return 0;
00242             }
00243         }
00244 
00246         public override ulong lastActiveOrigin { get { return lastActionData.activeOrigin; } }
00247 
00249         public override bool active { get { return activeBinding && action.actionSet.IsActive(inputSource); } }
