00260 
00261 
00262         protected InputAnalogActionData_t actionData = new InputAnalogActionData_t();
00263         protected InputAnalogActionData_t lastActionData = new InputAnalogActionData_t();
00264 
00265         protected SteamVR_Action_Vector3 vector3Action;
00266 
00270         public override void Preinitialize(SteamVR_Action wrappingAction, SteamVR_Input_Sources forInputSource)
00271         {
00272             base.Preinitialize(wrappingAction, forInputSource);
00273             vector3Action = (SteamVR_Action_Vector3)wrappingAction;
00274         }
00275 
00280         public override void Initialize()
00281         {
00282             base.Initialize();
00283 
00284             if (actionData_size == 0)
00285                 actionData_size = (uint)Marshal.SizeOf(typeof(InputAnalogActionData_t));
00286         }
00287 
00291         public override void UpdateValue()
00292         {
00293             lastActionData = actionData;
00294             lastActive = active;
00295             lastAxis = axis;
00296             lastDelta = delta;
00297 
00298             EVRInputError err = OpenVR.Input.GetAnalogActionData(handle, ref actionData, actionData_size, SteamVR_Input_Source.GetHandle(inputSource));
00299             if (err != EVRInputError.None)
00300                 Debug.LogError("<b>[SteamVR]</b> GetAnalogActionData error (" + fullPath + "): " + err.ToString() + " handle: " + handle.ToString());
00301 
00302             updateTime = Time.realtimeSinceStartup;
00303             axis = new Vector3(actionData.x, actionData.y, actionData.z);
00304             delta = new Vector3(actionData.deltaX, actionData.deltaY, actionData.deltaZ);
00305 
00306             changed = false;
00307 
00308             if (active)
00309             {
00310                 if (delta.magnitude > changeTolerance)
00311                 {
00312                     changed = true;
00313                     changedTime = Time.realtimeSinceStartup + actionData.fUpdateTime; //fUpdateTime is the time from the time the action was called that the action changed
00314 
00315                     if (onChange != null)
00316                         onChange.Invoke(vector3Action, inputSource, axis, delta);
00317                 }
00318 
00319                 if (axis != Vector3.zero)
00320                 {
00321                     if (onAxis != null)
00322                         onAxis.Invoke(vector3Action, inputSource, axis, delta);
00323                 }
00324 
00325                 if (onUpdate != null)
00326                 {
00327                     onUpdate.Invoke(vector3Action, inputSource, axis, delta);
00328                 }
00329             }
00330 
00331 
00332             if (onActiveBindingChange != null && lastActiveBinding != activeBinding)
00333                 onActiveBindingChange.Invoke(vector3Action, inputSource, activeBinding);
00334 
00335             if (onActiveChange != null && lastActive != active)
00336                 onActiveChange.Invoke(vector3Action, inputSource, activeBinding);
00337         }
00338     }
