00391         public void PreInitialize()
00392         {
00393             SteamVR_Input_Sources[] sources = SteamVR_Input_Source.GetAllSources();
00394 
00395             for (int sourceIndex = 0; sourceIndex < sources.Length; sourceIndex++)
00396             {
00397                 SteamVR_Input_Sources source = sources[sourceIndex];
00398                 rawSetActive.Add(source, false);
00399                 rawSetLastChanged.Add(source, 0);
00400                 rawSetPriority.Add(source, 0);
00401             }
00402         }
00403 
00404         public void FinishPreInitialize()
00405         {
00406             List<SteamVR_Action> allActionsList = new List<SteamVR_Action>();
00407             List<ISteamVR_Action_In> nonVisualInActionsList = new List<ISteamVR_Action_In>();
00408             List<ISteamVR_Action_In> visualActionsList = new List<ISteamVR_Action_In>();
00409             List<SteamVR_Action_Pose> poseActionsList = new List<SteamVR_Action_Pose>();
00410             List<SteamVR_Action_Skeleton> skeletonActionsList = new List<SteamVR_Action_Skeleton>();
00411             List<ISteamVR_Action_Out> outActionList = new List<ISteamVR_Action_Out>();
00412 
00413             if (SteamVR_Input.actions == null)
00414             {
00415                 Debug.LogError("<b>[SteamVR Input]</b> Actions not initialized!");
00416                 return;
00417             }
00418 
00419             for (int actionIndex = 0; actionIndex < SteamVR_Input.actions.Length; actionIndex++)
00420             {
00421                 SteamVR_Action action = SteamVR_Input.actions[actionIndex];
00422 
00423                 if (action.actionSet.GetActionSetData() == this)
00424                 {
00425                     allActionsList.Add(action);
00426 
00427                     if (action is ISteamVR_Action_Boolean || action is ISteamVR_Action_Single || action is ISteamVR_Action_Vector2 || action is ISteamVR_Action_Vector3)
00428                     {
00429                         nonVisualInActionsList.Add((ISteamVR_Action_In)action);
00430                     }
00431                     else if (action is SteamVR_Action_Pose)
00432                     {
00433                         visualActionsList.Add((ISteamVR_Action_In)action);
00434                         poseActionsList.Add((SteamVR_Action_Pose)action);
00435                     }
00436                     else if (action is SteamVR_Action_Skeleton)
00437                     {
00438                         visualActionsList.Add((ISteamVR_Action_In)action);
00439                         skeletonActionsList.Add((SteamVR_Action_Skeleton)action);
00440                     }
00441                     else if (action is ISteamVR_Action_Out)
00442                     {
00443                         outActionList.Add((ISteamVR_Action_Out)action);
00444                     }
00445                     else
00446                     {
00447                         Debug.LogError("<b>[SteamVR Input]</b> Action doesn't implement known interface: " + action.fullPath);
00448                     }
00449                 }
00450             }
00451 
00452             allActions = allActionsList.ToArray();
00453             nonVisualInActions = nonVisualInActionsList.ToArray();
00454             visualActions = visualActionsList.ToArray();
00455             poseActions = poseActionsList.ToArray();
00456             skeletonActions = skeletonActionsList.ToArray();
00457             outActionArray = outActionList.ToArray();
00458         }
00459 
00460         public void Initialize()
00461         {
00462             ulong newHandle = 0;
00463             EVRInputError err = OpenVR.Input.GetActionSetHandle(fullPath.ToLower(), ref newHandle);
00464             handle = newHandle;
00465 
00466             if (err != EVRInputError.None)
00467                 Debug.LogError("<b>[SteamVR]</b> GetActionSetHandle (" + fullPath + ") error: " + err.ToString());
00468 
00469             initialized = true;
00470         }
