00501         public void Activate(SteamVR_Input_Sources activateForSource = SteamVR_Input_Sources.Any, int priority = 0, bool disableAllOtherActionSets = false)
00502         {
00503             if (disableAllOtherActionSets)
00504                 SteamVR_ActionSet_Manager.DisableAllActionSets();
00505 
00506             if (rawSetActive[activateForSource] == false)
00507             {
00508                 rawSetActive[activateForSource] = true;
00509                 SteamVR_ActionSet_Manager.SetChanged();
00510 
00511                 rawSetLastChanged[activateForSource] = Time.realtimeSinceStartup;
00512             }
00513 
00514             if (rawSetPriority[activateForSource] != priority)
00515             {
00516                 rawSetPriority[activateForSource] = priority;
00517                 SteamVR_ActionSet_Manager.SetChanged();
00518 
00519                 rawSetLastChanged[activateForSource] = Time.realtimeSinceStartup;
00520             }
00521         }
00522 
00526         public void Deactivate(SteamVR_Input_Sources forSource = SteamVR_Input_Sources.Any)
00527         {
00528             if (rawSetActive[forSource] != false)
00529             {
00530                 rawSetLastChanged[forSource] = Time.realtimeSinceStartup;
00531                 SteamVR_ActionSet_Manager.SetChanged();
00532             }
00533 
00534             rawSetActive[forSource] = false;
00535             rawSetPriority[forSource] = 0;
00536         }
