00112         private void SteamVR_Behaviour_Pose_OnUpdate(SteamVR_Action_Pose fromAction, SteamVR_Input_Sources fromSource)
00113         {
00114             UpdateHistoryBuffer();
00115 
00116             UpdateTransform();
00117 
00118             if (onTransformUpdated != null)
00119                 onTransformUpdated.Invoke(this, inputSource);
00120             if (onTransformUpdatedEvent != null)
00121                 onTransformUpdatedEvent.Invoke(this, inputSource);
00122         }
00123         protected virtual void UpdateTransform()
00124         {
00125             CheckDeviceIndex();
00126 
00127             if (origin != null)
00128             {
00129                 transform.position = origin.transform.TransformPoint(poseAction[inputSource].localPosition);
00130                 transform.rotation = origin.rotation * poseAction[inputSource].localRotation;
00131             }
00132             else
00133             {
00134                 transform.localPosition = poseAction[inputSource].localPosition;
00135                 transform.localRotation = poseAction[inputSource].localRotation;
00136             }
00137         }
