00165         protected virtual void CheckDeviceIndex()
00166         {
00167             if (poseAction[inputSource].active && poseAction[inputSource].deviceIsConnected)
00168             {
00169                 int currentDeviceIndex = (int)poseAction[inputSource].trackedDeviceIndex;
00170 
00171                 if (deviceIndex != currentDeviceIndex)
00172                 {
00173                     deviceIndex = currentDeviceIndex;
00174 
00175                     if (broadcastDeviceChanges)
00176                     {
00177                         this.gameObject.BroadcastMessage("SetInputSource", inputSource, SendMessageOptions.DontRequireReceiver);
00178                         this.gameObject.BroadcastMessage("SetDeviceIndex", deviceIndex, SendMessageOptions.DontRequireReceiver);
00179                     }
00180 
00181                     if (onDeviceIndexChanged != null)
00182                         onDeviceIndexChanged.Invoke(this, inputSource, deviceIndex);
00183                     if (onDeviceIndexChangedEvent != null)
00184                         onDeviceIndexChangedEvent.Invoke(this, inputSource, deviceIndex);
00185                 }
00186             }
00187         }
00188 
00192         public int GetDeviceIndex()
00193         {
00194             if (deviceIndex == -1)
00195                 CheckDeviceIndex();
