00219         public void GetEstimatedPeakVelocities(out Vector3 velocity, out Vector3 angularVelocity)
00220         {
00221             int top = historyBuffer.GetTopVelocity(10, 1);
00222 
00223             historyBuffer.GetAverageVelocities(out velocity, out angularVelocity, 2, top);
00224         }
