00226         protected int lastFrameUpdated;
00227         protected void UpdateHistoryBuffer()
00228         {
00229             int currentFrame = Time.frameCount;
00230             if (lastFrameUpdated != currentFrame)
00231             {
00232                 historyBuffer.Update(poseAction[inputSource].localPosition, poseAction[inputSource].localRotation, poseAction[inputSource].velocity, poseAction[inputSource].angularVelocity);
00233                 lastFrameUpdated = currentFrame;
00234             }
00235         }
