00287         protected virtual void UpdateSkeleton()
00288         {
00289             if (skeletonAction == null || skeletonAction.active == false)
00290                 return;
00291 
00292             if (updatePose)
00293                 UpdatePose();
00294 
00295             if (blendPoser != null && skeletonBlend < 1)
00296             {
00297                 blendSnapshot = blendPoser.GetBlendedPose(this);
00298             }
00299 
00300             if (rangeOfMotionBlendRoutine == null)
00301             {
00302                 if (temporaryRangeOfMotion != null)
00303                     skeletonAction.SetRangeOfMotion(temporaryRangeOfMotion.Value);
00304                 else
00305                     skeletonAction.SetRangeOfMotion(rangeOfMotion); //this may be a frame behind
00306 
00307                 UpdateSkeletonTransforms();
00308             }
00309         }
