00399         protected IEnumerator DoBlendRoutine(float blendToAmount, float overTime)
00400         {
00401             float startTime = Time.time;
00402             float endTime = startTime + overTime;
00403 
00404             float startAmount = skeletonBlend;
00405 
00406             while (Time.time < endTime)
00407             {
00408                 yield return null;
00409                 skeletonBlend = Mathf.Lerp(startAmount, blendToAmount, (Time.time - startTime) / overTime);
00410             }
00411 
00412             skeletonBlend = blendToAmount;
00413             blendRoutine = null;
00414         }
00415 
