00467         protected IEnumerator DoRangeOfMotionBlend(EVRSkeletalMotionRange oldRangeOfMotion, EVRSkeletalMotionRange newRangeOfMotion, float overTime)
00468         {
00469             float startTime = Time.time;
00470             float endTime = startTime + overTime;
00471 
00472             Vector3[] oldBonePositions;
00473             Quaternion[] oldBoneRotations;
00474 
00475             Vector3[] newBonePositions;
00476             Quaternion[] newBoneRotations;
00477 
00478             while (Time.time < endTime)
00479             {
00480                 yield return null;
00481                 float lerp = (Time.time - startTime) / overTime;
00482 
00483                 if (skeletonBlend > 0)
00484                 {
00485                     skeletonAction.SetRangeOfMotion(oldRangeOfMotion);
00486                     skeletonAction.UpdateValueWithoutEvents();
00487                     oldBonePositions = (Vector3[])GetBonePositions().Clone();
00488                     oldBoneRotations = (Quaternion[])GetBoneRotations().Clone();
00489 
00490                     skeletonAction.SetRangeOfMotion(newRangeOfMotion);
00491                     skeletonAction.UpdateValueWithoutEvents();
00492                     newBonePositions = GetBonePositions();
00493                     newBoneRotations = GetBoneRotations();
00494 
00495                     for (int boneIndex = 0; boneIndex < bones.Length; boneIndex++)
00496                     {
00497                         if (bones[boneIndex] == null)
00498                             continue;
00499 
00500                         if (SteamVR_Utils.IsValid(newBoneRotations[boneIndex]) == false || SteamVR_Utils.IsValid(oldBoneRotations[boneIndex]) == false)
00501                         {
00502                             continue;
00503                         }
00504 
00505                         Vector3 blendedRangeOfMotionPosition = Vector3.Lerp(oldBonePositions[boneIndex], newBonePositions[boneIndex], lerp);
00506                         Quaternion blendedRangeOfMotionRotation = Quaternion.Lerp(oldBoneRotations[boneIndex], newBoneRotations[boneIndex], lerp);
00507 
00508                         if (skeletonBlend < 1)
00509                         {
00510                             if (blendPoser != null)
00511                             {
00512                                 SetBonePosition(boneIndex, Vector3.Lerp(blendSnapshot.bonePositions[boneIndex], blendedRangeOfMotionPosition, skeletonBlend));
00513                                 SetBoneRotation(boneIndex, Quaternion.Lerp(GetBlendPoseForBone(boneIndex, blendedRangeOfMotionRotation), blendedRangeOfMotionRotation, skeletonBlend));
00514                             }
00515                             else
00516                             {
00517                                 SetBonePosition(boneIndex, Vector3.Lerp(bones[boneIndex].localPosition, blendedRangeOfMotionPosition, skeletonBlend));
00518                                 SetBoneRotation(boneIndex, Quaternion.Lerp(bones[boneIndex].localRotation, blendedRangeOfMotionRotation, skeletonBlend));
00519                             }
00520                         }
00521                         else
00522                         {
00523                             SetBonePosition(boneIndex, blendedRangeOfMotionPosition);
00524                             SetBoneRotation(boneIndex, blendedRangeOfMotionRotation);
00525                         }
00526                     }
00527                 }
00528 
00529                 if (onBoneTransformsUpdated != null)
00530                     onBoneTransformsUpdated.Invoke(this, inputSource);
00531                 if (onBoneTransformsUpdatedEvent != null)
00532                     onBoneTransformsUpdatedEvent.Invoke(this, inputSource);
00533 
00534             }
00535 
00536             rangeOfMotionBlendRoutine = null;
00537         }
