00545         protected virtual void UpdateSkeletonTransforms()
00546         {
00547             Vector3[] bonePositions = GetBonePositions();
00548             Quaternion[] boneRotations = GetBoneRotations();
00549 
00550             if (skeletonBlend <= 0)
00551             {
00552                 if (blendPoser != null)
00553                 {
00554                     SteamVR_Skeleton_Pose_Hand mainPose = blendPoser.skeletonMainPose.GetHand(inputSource);
00555                     for (int boneIndex = 0; boneIndex < bones.Length; boneIndex++)
00556                     {
00557                         if (bones[boneIndex] == null)
00558                             continue;
00559 
00560                         if ((boneIndex == SteamVR_Skeleton_JointIndexes.wrist && mainPose.ignoreWristPoseData) ||
00561                             (boneIndex == SteamVR_Skeleton_JointIndexes.root && mainPose.ignoreRootPoseData))
00562                         {
00563                             SetBonePosition(boneIndex, bonePositions[boneIndex]);
00564                             SetBoneRotation(boneIndex, boneRotations[boneIndex]);
00565                         }
00566                         else
00567                         {
00568                             Quaternion poseRotation = GetBlendPoseForBone(boneIndex, boneRotations[boneIndex]);
00569 
00570                             SetBonePosition(boneIndex, blendSnapshot.bonePositions[boneIndex]);
00571                             SetBoneRotation(boneIndex, poseRotation);
00572                         }
00573                     }
00574                 }
00575             }
00576             else if (skeletonBlend >= 1)
00577             {
00578                 for (int boneIndex = 0; boneIndex < bones.Length; boneIndex++)
00579                 {
00580                     if (bones[boneIndex] == null)
00581                         continue;
00582 
00583                     SetBonePosition(boneIndex, bonePositions[boneIndex]);
00584                     SetBoneRotation(boneIndex, boneRotations[boneIndex]);
00585                 }
00586             }
00587             else
00588             {
00589                 for (int boneIndex = 0; boneIndex < bones.Length; boneIndex++)
00590                 {
00591                     if (bones[boneIndex] == null)
00592                         continue;
00593                     
00594                     if (blendPoser != null)
00595                     {
00596                         SteamVR_Skeleton_Pose_Hand mainPose = blendPoser.skeletonMainPose.GetHand(inputSource);
00597 
00598                         if ((boneIndex == SteamVR_Skeleton_JointIndexes.wrist && mainPose.ignoreWristPoseData) ||
00599                             (boneIndex == SteamVR_Skeleton_JointIndexes.root && mainPose.ignoreRootPoseData))
00600                         {
00601                             SetBonePosition(boneIndex, bonePositions[boneIndex]);
00602                             SetBoneRotation(boneIndex, boneRotations[boneIndex]);
00603                         }
00604                         else
00605                         {
00606                             Quaternion poseRotation = GetBlendPoseForBone(boneIndex, boneRotations[boneIndex]);
00607 
00608                             SetBonePosition(boneIndex, Vector3.Lerp(blendSnapshot.bonePositions[boneIndex], bonePositions[boneIndex], skeletonBlend));
00609                             SetBoneRotation(boneIndex, Quaternion.Lerp(poseRotation, boneRotations[boneIndex], skeletonBlend));
00610                         }
00611                     }
00612                     else
00613                     {
00614                         SetBonePosition(boneIndex, Vector3.Lerp(bones[boneIndex].localPosition, bonePositions[boneIndex], skeletonBlend));
00615                         SetBoneRotation(boneIndex, Quaternion.Lerp(bones[boneIndex].localRotation, boneRotations[boneIndex], skeletonBlend));
00616                     }
00617                 }
00618             }
00619 
00620 
00621             if (onBoneTransformsUpdated != null)
00622                 onBoneTransformsUpdated.Invoke(this, inputSource);
00623             if (onBoneTransformsUpdatedEvent != null)
00624                 onBoneTransformsUpdatedEvent.Invoke(this, inputSource);
00625         }
