00677         protected Vector3[] GetBonePositions()
00678         {
00679             Vector3[] rawSkeleton = skeletonAction.GetBonePositions();
00680             if (mirroring == MirrorType.LeftToRight || mirroring == MirrorType.RightToLeft)
00681             {
00682                 for (int boneIndex = 0; boneIndex < rawSkeleton.Length; boneIndex++)
00683                 {
00684                     if (boneIndex == SteamVR_Skeleton_JointIndexes.wrist || IsMetacarpal(boneIndex))
00685                     {
00686                         rawSkeleton[boneIndex].Scale(new Vector3(-1, 1, 1));
00687                     }
00688                     else if (boneIndex != SteamVR_Skeleton_JointIndexes.root)
00689                     {
00690                         rawSkeleton[boneIndex] = rawSkeleton[boneIndex] * -1;
00691                     }
00692                 }
00693             }
00694 
00695             return rawSkeleton;
00696         }
