00698         protected Quaternion rightFlipAngle = Quaternion.AngleAxis(180, Vector3.right);
00699         protected Quaternion[] GetBoneRotations()
00700         {
00701             Quaternion[] rawSkeleton = skeletonAction.GetBoneRotations();
00702             if (mirroring == MirrorType.LeftToRight || mirroring == MirrorType.RightToLeft)
00703             {
00704                 for (int boneIndex = 0; boneIndex < rawSkeleton.Length; boneIndex++)
00705                 {
00706                     if (boneIndex == SteamVR_Skeleton_JointIndexes.wrist)
00707                     {
00708                         rawSkeleton[boneIndex].y = rawSkeleton[boneIndex].y * -1;
00709                         rawSkeleton[boneIndex].z = rawSkeleton[boneIndex].z * -1;
00710                     }
00711 
00712                     if (IsMetacarpal(boneIndex))
00713                     {
00714                         rawSkeleton[boneIndex] = rightFlipAngle * rawSkeleton[boneIndex];
00715                     }
00716                 }
00717             }
00718 
00719             return rawSkeleton;
00720         }
00721         
00722         protected virtual void UpdatePose()
00723         {
00724             if (skeletonAction == null)
00725                 return;
00726 
00727             Vector3 skeletonPosition = skeletonAction.GetLocalPosition();
00728             Quaternion skeletonRotation = skeletonAction.GetLocalRotation();
00729             if (origin == null)
00730             {
00731                 if (this.transform.parent != null)
00732                 {
00733                     skeletonPosition = this.transform.parent.TransformPoint(skeletonPosition);
00734                     skeletonRotation = this.transform.parent.rotation * skeletonRotation;
00735                 }
00736             }
00737             else
00738             {
00739                 skeletonPosition = origin.TransformPoint(skeletonPosition);
00740                 skeletonRotation = origin.rotation * skeletonRotation;
00741             }
00742 
00743             if (skeletonAction.poseChanged)
00744             {
00745                 if (onTransformChanged != null)
00746                     onTransformChanged.Invoke(this, inputSource);
00747                 if (onTransformChangedEvent != null)
00748                     onTransformChangedEvent.Invoke(this, inputSource);
00749             }
00750 
00751             this.transform.position = skeletonPosition;
00752             this.transform.rotation = skeletonRotation;
00753 
00754             if (onTransformUpdated != null)
00755                 onTransformUpdated.Invoke(this, inputSource);
00756         }
00757 
00762         public void ForceToReferencePose(EVRSkeletalReferencePose referencePose)
00763         {
00764             bool temporarySession = false;
00765             if (Application.isEditor && Application.isPlaying == false)
00766             {
00767                 temporarySession = SteamVR.InitializeTemporarySession(true);
00768                 Awake();
00769 
00770 #if UNITY_EDITOR
00771                 //gotta wait a bit for steamvr input to startup //todo: implement steamvr_input.isready
00772                 string title = "SteamVR";
00773                 string text = "Getting reference pose...";
00774                 float msToWait = 3000;
00775                 float increment = 100;
00776                 for (float timer = 0; timer < msToWait; timer += increment)
00777                 {
00778                     bool cancel = UnityEditor.EditorUtility.DisplayCancelableProgressBar(title, text, timer / msToWait);
00779                     if (cancel)
00780                     {
00781                         UnityEditor.EditorUtility.ClearProgressBar();
00782 
00783                         if (temporarySession)
00784                             SteamVR.ExitTemporarySession();
00785                         return;
00786                     }
00787                     System.Threading.Thread.Sleep((int)increment);
00788                 }
00789                 UnityEditor.EditorUtility.ClearProgressBar();
00790 #endif
00791 
00792                 skeletonAction.actionSet.Activate();
00793 
00794                 SteamVR_ActionSet_Manager.UpdateActionStates(true);
00795 
00796                 skeletonAction.UpdateValueWithoutEvents();
00797             }
00798 
00799             if (skeletonAction.active == false)
00800             {
00801                 Debug.LogError("<b>[SteamVR Input]</b> Please turn on your " + inputSource.ToString() + " controller and ensure SteamVR is open.");
00802                 return;
00803             }
00804 
00805             SteamVR_Utils.RigidTransform[] transforms = skeletonAction.GetReferenceTransforms(EVRSkeletalTransformSpace.Parent, referencePose);
00806 
00807             if (transforms == null || transforms.Length == 0)
00808             {
00809                 Debug.LogError("<b>[SteamVR Input]</b> Unable to get the reference transform for " + inputSource.ToString() + ". Please make sure SteamVR is open and both controllers are connected.");
00810             }
00811 
00812             for (int boneIndex = 0; boneIndex < transforms.Length; boneIndex++)
00813             {
00814                 bones[boneIndex].localPosition = transforms[boneIndex].pos;
00815                 bones[boneIndex].localRotation = transforms[boneIndex].rot;
00816             }
00817 
00818             if (temporarySession)
00819                 SteamVR.ExitTemporarySession();
00820         }
00821 
00822         protected bool IsMetacarpal(int boneIndex)
00823         {
00824             return (boneIndex == SteamVR_Skeleton_JointIndexes.indexMetacarpal ||
00825                 boneIndex == SteamVR_Skeleton_JointIndexes.middleMetacarpal ||
00826                 boneIndex == SteamVR_Skeleton_JointIndexes.ringMetacarpal ||
00827                 boneIndex == SteamVR_Skeleton_JointIndexes.pinkyMetacarpal ||
00828                 boneIndex == SteamVR_Skeleton_JointIndexes.thumbMetacarpal);
00829         }
