00001 //======= Copyright (c) Valve Corporation, All rights reserved. ===============
00002 //
00003 // Purpose: CameraFade script adapted to work with SteamVR.
00004 //
00005 // Usage:   Add to your top level SteamVR_Camera (the one with ApplyDistoration
00006 //          checked) and drag a reference to this component into SteamVR_Camera
00007 //          RenderComponents list.  Then call the static helper function
00008 //          SteamVR_Fade.Start with the desired color and duration.
00009 //          Use a duration of zero to set the start color.
00010 //
00011 // Example: Fade down from black over one second.
00012 //          SteamVR_Fade.Start(Color.black, 0);
00013 //          SteamVR_Fade.Start(Color.clear, 1);
00014 //
00015 // Note:    This component is provided to fade out a single camera layer's
00016 //          scene view.  If instead you want to fade the entire view, use:
00017 //          SteamVR_Fade.View(Color.black, 1);
00018 //          (Does not affect the game view, however.)
00019 //
00020 //=============================================================================
00021 
00022 using UnityEngine;
00023 using Valve.VR;
00024 
00025 namespace Valve.VR
00026 {
00027     public class SteamVR_Fade : MonoBehaviour
00028     {
00029         private Color currentColor = new Color(0, 0, 0, 0); // default starting color: black and fully transparent
00030         private Color targetColor = new Color(0, 0, 0, 0);  // default target color: black and fully transparent
00031         private Color deltaColor = new Color(0, 0, 0, 0);   // the delta-color is basically the "speed / second" at which the current color should change
00032         private bool fadeOverlay = false;
00033 
00034         static public void Start(Color newColor, float duration, bool fadeOverlay = false)
00035         {
00036             SteamVR_Events.Fade.Send(newColor, duration, fadeOverlay);
00037         }
00038 
00039         static public void View(Color newColor, float duration)
00040         {
00041             var compositor = OpenVR.Compositor;
00042             if (compositor != null)
00043                 compositor.FadeToColor(duration, newColor.r, newColor.g, newColor.b, newColor.a, false);
00044         }
00045 
00046 #if TEST_FADE_VIEW
00047     void Update()
00048     {
00049         if (Input.GetKeyDown(KeyCode.Space))
00050         {
00051             SteamVR_Fade.View(Color.black, 0);
00052             SteamVR_Fade.View(Color.clear, 1);
00053         }
00054     }
00055 #endif
00056 
00057         public void OnStartFade(Color newColor, float duration, bool fadeOverlay)
00058         {
00059             if (duration > 0.0f)
00060             {
00061                 targetColor = newColor;
00062                 deltaColor = (targetColor - currentColor) / duration;
00063             }
00064             else
00065             {
00066                 currentColor = newColor;
00067             }
00068         }
00069 
00070         static Material fadeMaterial = null;
00071         static int fadeMaterialColorID = -1;
00072 
00073         void OnEnable()
00074         {
00075             if (fadeMaterial == null)
00076             {
00077                 fadeMaterial = new Material(Shader.Find("Custom/SteamVR_Fade"));
00078                 fadeMaterialColorID = Shader.PropertyToID("fadeColor");
00079             }
00080 
00081             SteamVR_Events.Fade.Listen(OnStartFade);
00082             SteamVR_Events.FadeReady.Send();
00083         }
00084 
00085         void OnDisable()
00086         {
00087             SteamVR_Events.Fade.Remove(OnStartFade);
00088         }
00089 
00090         void OnPostRender()
00091         {
00092             if (currentColor != targetColor)
00093             {
00094                 // if the difference between the current alpha and the desired alpha is smaller than delta-alpha * deltaTime, then we're pretty much done fading:
00095                 if (Mathf.Abs(currentColor.a - targetColor.a) < Mathf.Abs(deltaColor.a) * Time.deltaTime)
00096                 {
00097                     currentColor = targetColor;
00098                     deltaColor = new Color(0, 0, 0, 0);
00099                 }
00100                 else
00101                 {
00102                     currentColor += deltaColor * Time.deltaTime;
00103                 }
00104 
00105                 if (fadeOverlay)
00106                 {
00107                     var overlay = SteamVR_Overlay.instance;
00108                     if (overlay != null)
00109                     {
00110                         overlay.alpha = 1.0f - currentColor.a;
00111                     }
00112                 }
00113             }
00114 
00115             if (currentColor.a > 0 && fadeMaterial)
00116             {
00117                 fadeMaterial.SetColor(fadeMaterialColorID, currentColor);
00118                 fadeMaterial.SetPass(0);
00119                 GL.Begin(GL.QUADS);
00120 
00121                 GL.Vertex3(-1, -1, 0);
00122                 GL.Vertex3(1, -1, 0);
00123                 GL.Vertex3(1, 1, 0);
00124                 GL.Vertex3(-1, 1, 0);
00125                 GL.End();
00126             }
00127         }
00128     }
00129 }
