section.programlisting:
00001 //======= Copyright (c) Valve Corporation, All rights reserved. ===============
00002 //
00003 // Purpose: Generates a mesh based on field of view.
00004 //
00005 //=============================================================================
00006 
00007 using UnityEngine;
00008 using Valve.VR;
00009 
00010 namespace Valve.VR
00011 {
00012     [ExecuteInEditMode, RequireComponent(typeof(MeshRenderer), typeof(MeshFilter))]
00013     public class SteamVR_Frustum : MonoBehaviour
00014     {
00015         public SteamVR_TrackedObject.EIndex index;
00016 
00017         public float fovLeft = 45, fovRight = 45, fovTop = 45, fovBottom = 45, nearZ = 0.5f, farZ = 2.5f;
00018 
00019         public void UpdateModel()
00020         {
00021             fovLeft = Mathf.Clamp(fovLeft, 1, 89);
00022             fovRight = Mathf.Clamp(fovRight, 1, 89);
00023             fovTop = Mathf.Clamp(fovTop, 1, 89);
00024             fovBottom = Mathf.Clamp(fovBottom, 1, 89);
00025             farZ = Mathf.Max(farZ, nearZ + 0.01f);
00026             nearZ = Mathf.Clamp(nearZ, 0.01f, farZ - 0.01f);
00027 
00028             var lsin = Mathf.Sin(-fovLeft * Mathf.Deg2Rad);
00029             var rsin = Mathf.Sin(fovRight * Mathf.Deg2Rad);
00030             var tsin = Mathf.Sin(fovTop * Mathf.Deg2Rad);
00031             var bsin = Mathf.Sin(-fovBottom * Mathf.Deg2Rad);
00032 
00033             var lcos = Mathf.Cos(-fovLeft * Mathf.Deg2Rad);
00034             var rcos = Mathf.Cos(fovRight * Mathf.Deg2Rad);
00035             var tcos = Mathf.Cos(fovTop * Mathf.Deg2Rad);
00036             var bcos = Mathf.Cos(-fovBottom * Mathf.Deg2Rad);
00037 
00038             var corners = new Vector3[] {
00039             new Vector3(lsin * nearZ / lcos, tsin * nearZ / tcos, nearZ), //tln
00040             new Vector3(rsin * nearZ / rcos, tsin * nearZ / tcos, nearZ), //trn
00041             new Vector3(rsin * nearZ / rcos, bsin * nearZ / bcos, nearZ), //brn
00042             new Vector3(lsin * nearZ / lcos, bsin * nearZ / bcos, nearZ), //bln
00043             new Vector3(lsin * farZ  / lcos, tsin * farZ  / tcos, farZ ), //tlf
00044             new Vector3(rsin * farZ  / rcos, tsin * farZ  / tcos, farZ ), //trf
00045             new Vector3(rsin * farZ  / rcos, bsin * farZ  / bcos, farZ ), //brf
00046             new Vector3(lsin * farZ  / lcos, bsin * farZ  / bcos, farZ ), //blf
00047         };
00048 
00049             var triangles = new int[] {
00050         //  0, 1, 2, 0, 2, 3, // near
00051         //  0, 2, 1, 0, 3, 2, // near
00052         //  4, 5, 6, 4, 6, 7, // far
00053         //  4, 6, 5, 4, 7, 6, // far
00054             0, 4, 7, 0, 7, 3, // left
00055             0, 7, 4, 0, 3, 7, // left
00056             1, 5, 6, 1, 6, 2, // right
00057             1, 6, 5, 1, 2, 6, // right
00058             0, 4, 5, 0, 5, 1, // top
00059             0, 5, 4, 0, 1, 5, // top
00060             2, 3, 7, 2, 7, 6, // bottom
00061             2, 7, 3, 2, 6, 7, // bottom
00062         };
00063 
00064             int j = 0;
00065             var vertices = new Vector3[triangles.Length];
00066             var normals = new Vector3[triangles.Length];
00067             for (int i = 0; i < triangles.Length / 3; i++)
00068             {
00069                 var a = corners[triangles[i * 3 + 0]];
00070                 var b = corners[triangles[i * 3 + 1]];
00071                 var c = corners[triangles[i * 3 + 2]];
00072                 var n = Vector3.Cross(b - a, c - a).normalized;
00073                 normals[i * 3 + 0] = n;
00074                 normals[i * 3 + 1] = n;
00075                 normals[i * 3 + 2] = n;
00076                 vertices[i * 3 + 0] = a;
00077                 vertices[i * 3 + 1] = b;
00078                 vertices[i * 3 + 2] = c;
00079                 triangles[i * 3 + 0] = j++;
00080                 triangles[i * 3 + 1] = j++;
00081                 triangles[i * 3 + 2] = j++;
00082             }
00083 
00084             var mesh = new Mesh();
00085             mesh.vertices = vertices;
00086             mesh.normals = normals;
00087             mesh.triangles = triangles;
00088 
00089             GetComponent<MeshFilter>().mesh = mesh;
00090         }
00091 
00092         private void OnDeviceConnected(int i, bool connected)
00093         {
00094             if (i != (int)index)
00095                 return;
00096 
00097             GetComponent<MeshFilter>().mesh = null;
00098 
00099             if (connected)
00100             {
00101                 var system = OpenVR.System;
00102                 if (system != null && system.GetTrackedDeviceClass((uint)i) == ETrackedDeviceClass.TrackingReference)
00103                 {
00104                     var error = ETrackedPropertyError.TrackedProp_Success;
00105                     var result = system.GetFloatTrackedDeviceProperty((uint)i, ETrackedDeviceProperty.Prop_FieldOfViewLeftDegrees_Float, ref error);
00106                     if (error == ETrackedPropertyError.TrackedProp_Success)
00107                         fovLeft = result;
00108 
00109                     result = system.GetFloatTrackedDeviceProperty((uint)i, ETrackedDeviceProperty.Prop_FieldOfViewRightDegrees_Float, ref error);
00110                     if (error == ETrackedPropertyError.TrackedProp_Success)
00111                         fovRight = result;
00112 
00113                     result = system.GetFloatTrackedDeviceProperty((uint)i, ETrackedDeviceProperty.Prop_FieldOfViewTopDegrees_Float, ref error);
00114                     if (error == ETrackedPropertyError.TrackedProp_Success)
00115                         fovTop = result;
00116 
00117                     result = system.GetFloatTrackedDeviceProperty((uint)i, ETrackedDeviceProperty.Prop_FieldOfViewBottomDegrees_Float, ref error);
00118                     if (error == ETrackedPropertyError.TrackedProp_Success)
00119                         fovBottom = result;
00120 
00121                     result = system.GetFloatTrackedDeviceProperty((uint)i, ETrackedDeviceProperty.Prop_TrackingRangeMinimumMeters_Float, ref error);
00122                     if (error == ETrackedPropertyError.TrackedProp_Success)
00123                         nearZ = result;
00124 
00125                     result = system.GetFloatTrackedDeviceProperty((uint)i, ETrackedDeviceProperty.Prop_TrackingRangeMaximumMeters_Float, ref error);
00126                     if (error == ETrackedPropertyError.TrackedProp_Success)
00127                         farZ = result;
00128 
00129                     UpdateModel();
00130                 }
00131             }
00132         }
00133 
00134         void OnEnable()
00135         {
00136             GetComponent<MeshFilter>().mesh = null;
00137             SteamVR_Events.DeviceConnected.Listen(OnDeviceConnected);
00138         }
00139 
00140         void OnDisable()
00141         {
00142             SteamVR_Events.DeviceConnected.Remove(OnDeviceConnected);
00143             GetComponent<MeshFilter>().mesh = null;
00144         }
00145 
00146 #if UNITY_EDITOR
00147         void Update()
00148         {
00149             if (!Application.isPlaying)
00150                 UpdateModel();
00151         }
00152 #endif
00153     }
00154 }
