00122         private static void FindPreinitializeMethod()
00123         {
00124             Assembly[] assemblies = AppDomain.CurrentDomain.GetAssemblies();
00125             for (int assemblyIndex = 0; assemblyIndex < assemblies.Length; assemblyIndex++)
00126             {
00127                 Assembly assembly = assemblies[assemblyIndex];
00128                 Type type = assembly.GetType(SteamVR_Input_Generator_Names.fullActionsClassName);
00129                 if (type != null)
00130                 {
00131                     MethodInfo preinitMethodInfo = type.GetMethod(SteamVR_Input_Generator_Names.preinitializeMethodName);
00132                     if (preinitMethodInfo != null)
00133                     {
00134                         preinitMethodInfo.Invoke(null, null);
00135                         return;
00136                     }
00137                 }
00138             }
00139         }
