00146         public static void Initialize(bool force = false)
00147         {
00148             if (initialized == true && force == false)
00149                 return;
00150 
00151 #if UNITY_EDITOR
00152             CheckSetup();
00153             if (IsOpeningSetup())
00154                 return;
00155 #endif
00156 
00157             //Debug.Log("<b>[SteamVR]</b> Initializing SteamVR input...");
00158             initializing = true;
00159 
00160             startupFrame = Time.frameCount;
00161 
00162             SteamVR_ActionSet_Manager.Initialize();
00163             SteamVR_Input_Source.Initialize();
00164 
00165             for (int actionIndex = 0; actionIndex < actions.Length; actionIndex++)
00166             {
00167                 SteamVR_Action action = actions[actionIndex];
00168                 action.Initialize(true);
00169             }
00170 
00171             for (int actionSetIndex = 0; actionSetIndex < actionSets.Length; actionSetIndex++)
00172             {
00173                 SteamVR_ActionSet set = actionSets[actionSetIndex];
00174                 set.Initialize(true);
00175             }
00176 
00177             if (SteamVR_Settings.instance.activateFirstActionSetOnStart)
00178             {
00179                 if (actionSets.Length > 0)
00180                     actionSets[0].Activate();
00181                 else
00182                 {
00183                     Debug.LogError("<b>[SteamVR]</b> No action sets to activate.");
00184                 }
00185             }
00186 
00187             SteamVR_Action_Pose.SetTrackingUniverseOrigin(SteamVR_Settings.instance.trackingSpace);
00188 
00189             initialized = true;
00190             initializing = false;
00191             //Debug.Log("<b>[SteamVR]</b> Input initialization complete.");
00192         }
