00232         public static void Update()
00233         {
00234             if (initialized == false || isStartupFrame)
00235                 return;
00236 
00237             if (SteamVR.settings.IsInputUpdateMode(SteamVR_UpdateModes.OnUpdate))
00238             {
00239                 UpdateNonVisualActions();
00240             }
00241             if (SteamVR.settings.IsPoseUpdateMode(SteamVR_UpdateModes.OnUpdate))
00242             {
00243                 UpdateVisualActions();
00244             }
00245         }
00246 
00251         public static void LateUpdate()
00252         {
00253             if (initialized == false || isStartupFrame)
00254                 return;
00255 
00256             if (SteamVR.settings.IsInputUpdateMode(SteamVR_UpdateModes.OnLateUpdate))
00257             {
00258                 UpdateNonVisualActions();
00259             }
00260 
00261             if (SteamVR.settings.IsPoseUpdateMode(SteamVR_UpdateModes.OnLateUpdate))
00262             {
00263                 //update poses and skeleton
00264                 UpdateVisualActions();
00265             }
00266             else
00267             {
00268                 //force skeleton update so animation blending sticks
00269                 UpdateSkeletonActions(true);
00270             }
00271         }
00272 
