00440         // non-copy version
00441         public static SteamVR_Action GetBaseActionFromPath(string path, bool caseSensitive = false)
00442         {
00443             if (string.IsNullOrEmpty(path))
00444                 return null;
00445 
00446             if (caseSensitive)
00447             {
00448                 if (actionsByPath.ContainsKey(path))
00449                 {
00450                     return actionsByPath[path];
00451                 }
00452             }
00453             else
00454             {
00455                 if (actionsByPathCache.ContainsKey(path))
00456                 {
00457                     return actionsByPathCache[path];
00458                 }
00459                 else if (actionsByPath.ContainsKey(path))
00460                 {
00461                     actionsByPathCache.Add(path, actionsByPath[path]);
00462                     return actionsByPath[path];
00463                 }
00464                 else
00465                 {
00466                     string loweredPath = path.ToLower();
00467                     if (actionsByPathLowered.ContainsKey(loweredPath))
00468                     {
00469                         actionsByPathCache.Add(path, actionsByPathLowered[loweredPath]);
00470                         return actionsByPath[loweredPath];
00471                     }
00472                     else
00473                     {
00474                         actionsByPathCache.Add(path, null);
00475                     }
00476                 }
00477             }
00478 
00479             return null;
00480         }
