00589         public static SteamVR_Action GetBaseAction(string actionSetName, string actionName, bool caseSensitive = false)
00590         {
00591             if (actions == null)
00592             {
00593                 return null;
00594             }
00595 
00596             if (string.IsNullOrEmpty(actionSetName))
00597             {
00598                 for (int actionIndex = 0; actionIndex < actions.Length; actionIndex++)
00599                 {
00600                     if (caseSensitive)
00601                     {
00602                         if (actions[actionIndex].GetShortName() == actionName)
00603                             return actions[actionIndex];
00604                     }
00605                     else
00606                     {
00607                         if (string.Equals(actions[actionIndex].GetShortName(), actionName, StringComparison.CurrentCultureIgnoreCase))
00608                             return actions[actionIndex];
00609                     }
00610                 }
00611             }
00612             else
00613             {
00614                 SteamVR_ActionSet actionSet = GetActionSet(actionSetName, caseSensitive, true);
00615 
00616                 if (actionSet != null)
00617                 {
00618                     for (int actionIndex = 0; actionIndex < actionSet.allActions.Length; actionIndex++)
00619                     {
00620                         if (caseSensitive)
00621                         {
00622                             if (actionSet.allActions[actionIndex].GetShortName() == actionName)
00623                                 return actionSet.allActions[actionIndex];
00624                         }
00625                         else
00626                         {
00627                             if (string.Equals(actionSet.allActions[actionIndex].GetShortName(), actionName, StringComparison.CurrentCultureIgnoreCase))
00628                                 return actionSet.allActions[actionIndex];
00629                         }
00630                     }
00631                 }
00632             }
00633 
00634             return null;
00635         }
