00879         public static T GetActionSetFromPath<T>(string path, bool caseSensitive = false, bool returnsNulls = false) where T : SteamVR_ActionSet, new()
00880         {
00881             if (actionSets == null || actionSets[0] == null || string.IsNullOrEmpty(path))
00882             {
00883                 if (returnsNulls)
00884                     return null;
00885 
00886                 return SteamVR_ActionSet.Create<T>(path);
00887             }
00888 
00889             if (caseSensitive)
00890             {
00891                 if (actionSetsByPath.ContainsKey(path))
00892                 {
00893                     return actionSetsByPath[path].GetCopy<T>();
00894                 }
00895             }
00896             else
00897             {
00898                 if (actionSetsByPathCache.ContainsKey(path))
00899                 {
00900                     SteamVR_ActionSet set = actionSetsByPathCache[path];
00901                     if (set == null)
00902                         return null;
00903                     else
00904                         return set.GetCopy<T>();
00905                 }
00906                 else if (actionSetsByPath.ContainsKey(path))
00907                 {
00908                     actionSetsByPathCache.Add(path, actionSetsByPath[path]);
00909                     return actionSetsByPath[path].GetCopy<T>();
00910                 }
00911                 else
00912                 {
00913                     string loweredPath = path.ToLower();
00914                     if (actionSetsByPathLowered.ContainsKey(loweredPath))
00915                     {
00916                         actionSetsByPathCache.Add(path, actionSetsByPathLowered[loweredPath]);
00917                         return actionSetsByPath[loweredPath].GetCopy<T>();
00918                     }
00919                     else
00920                     {
00921                         actionSetsByPathCache.Add(path, null);
00922                     }
00923                 }
00924             }
00925 
00926             if (returnsNulls)
00927                 return null;
00928 
00929             return SteamVR_ActionSet.Create<T>(path);
00930         }
